import React, {useState, useEffect} from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';
import { NavigationContainer, Link } from '@react-navigation/native';
import { createDrawerNavigator, m } from '@react-navigation/drawer';
import HomeScreen from './screens/homeScreen';
import ContactUsScreen from './screens/contactScreen';
import AboutUsScreen from './screens/aboutScreen';
import { getProducts } from './services/productService';
import FontAwesome5 from 'react-native-vector-icons/FontAwesome5';

const Drawer = createDrawerNavigator();



function App() {

  const [products, setProducts] = useState([]);

  useEffect(() => {
    setProducts(getProducts());
  });

  return (
    <NavigationContainer>
      <Drawer.Navigator
        initialRouteName="Home_Screen"
        drawerPosition='left'
        drawerType="front"
        edgeWidth={100}
        hideStatusBar={false}
        overlayColor='#00000090'
        drawerStyle={{
          backgroundColor: '#e6e6e6',
          width: 250
        }}
        screenOptions={{
          headerShown: true,
          swipeEnabled: true,
          gestureEnabled: true,
          headerTitleAlign: 'center',
          headerStyle: {
            backgroundColor: '#7fad39'
          },
          headerTintColor: '#ffffff',
          headerTitleStyle: {
            fontSize: 25,
            fontWeight: 'bold'
          },
          drawerActiveTintColor: '#7fad39'
        }}
        drawerItems={products}
        drawerContent={props => <CustomDrawerContent {...props} />}
      >
        <Drawer.Screen
          name="Home_Screen"
          component={HomeScreen}
          options={{
            title: 'Home',
            drawerIcon: ({ focused }) => (
              <FontAwesome5
                name="home"
                size={focused ? 25 : 20}
                color={focused ? '#7fad39' : '#999999'}
              />
            )
          }}
        />
        <Drawer.Screen
          name="AboutUs_Screen"
          component={AboutUsScreen}
          options={{
            title: 'About Us',
            drawerIcon: ({ focused }) => (
              <FontAwesome5
                name="globe"
                size={focused ? 25 : 20}
                color={focused ? '#7fad39' : '#999999'}
              />
            )
          }}
        />
        <Drawer.Screen
          name="ContactUs_Screen"
          component={ContactUsScreen}
          options={{
            title: 'Contact Us',
            drawerIcon: ({ focused }) => (
              <FontAwesome5
                name="comment"
                size={focused ? 25 : 20}
                color={focused ? '#7fad39' : '#999999'}
              />
            )
          }}
        />




        {
          products.map((item) => {
            return (
              <Drawer.Screen 
                  key={item.id}
                  name={item.name}
                  component={HomeScreen}
                  options={{ 
                    title: item.name,
                    drawerIcon: ({ focused }) => (
                      <FontAwesome5
                        name="globe"
                        size={focused ? 25 : 20}
                        color={focused ? '#7fad39' : '#999999'}
                      />
                    )
                  }}
              />
          )
          })
        }    

      </Drawer.Navigator>

    </NavigationContainer> 

  )
}

const styles = StyleSheet.create({
  catContainer: {
    backgroundColor: 'rgba(127, 173, 57, 0.12)',
    borderRadius: 4,

    textAlign: 'center', 
  }
});

export default App;
