import React, { useEffect, useState, useContext } from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';

import { CartItemsContext } from '../context/CartItems';

export function ShoppingCart () {

  const {items, getTotalPrice} = useContext(CartItemsContext);

  function Totals() {
    let [total, setTotal] = useState(0);
    useEffect(() => {
      setTotal(getTotalPrice());
    });
    return (
       <View style={styles.cartLineTotal}>
          <Text style={[styles.lineLeft, styles.lineTotal]}>Total</Text>
          <Text style={styles.lineRight}>$ {total}</Text>
       </View>
    );
  }

  function renderItem({item}) {
    return (
        // <View style={styles.homeContainer}>
        //   <Text style={styles.homeHeading}>Welcome to React Native</Text>
        //   <Text style={styles.homeContent}>React Native is an open source framework for building Android and iOS applications using React and the app platform’s native capabilities. But first, let’s cover how components work in React Native.</Text>
        // </View>

       <View style={styles.cartLine}>
          <Text style={styles.lineLeft}>{item.product.name} x {item.qty}</Text>
          <Text style={styles.lineRight}>$ {item.totalPrice}</Text>
       </View>
    );
  }
  
  return (
    <FlatList
    style={styles.itemsList}
    contentContainerStyle={styles.itemsListContainer}
    data={items}
    renderItem={renderItem}
    keyExtractor={(item) => item.product.id.toString()}
    ListFooterComponent={Totals}
    />
    
  );
}

const styles = StyleSheet.create({
  cartCntr: {
    backgroundColor: '#ffffff',
    height: '100%'
  },
  cartLine: { 
    flexDirection: 'row',
    
  },
  cartLineTotal: { 
    flexDirection: 'row',
    borderTopColor: '#dddddd',
    borderTopWidth: 1,
    margin: 10,
    padding: 10
  },
  lineTotal: {
    fontWeight: 'bold',    
  },
  lineLeft: {
    fontSize: 14,  
    color:'#000' 
  },
  lineRight: { 
    flex: 1,
    fontSize: 14, 
    fontWeight: 'bold', 
    color:'#333333', 
    textAlign:'right',
  },
  itemsList: {
    backgroundColor: '#ffffff',
  },
  itemsListContainer: {
    backgroundColor: '#ffffff',
    backgroundColor: '#ffffff',
      margin: 12,
      padding: 16,
      fontFamily: 'arial',
      color: '#666',
      borderRadius: 16,
    shadowOpacity: 0.2,
    shadowRadius: 4,
    shadowColor: 'black',
    shadowOffset: {
      height: 0,
      width: 0,
    },
    elevation: 1,
  },
});
