import React, { useState } from 'react';
import { ErrorMessage } from "@hookform/error-message";
import { useForm } from 'react-hook-form';
import {
    StyleSheet,
    View,
    Text,
    SafeAreaView,
    TextInput,
    TouchableHighlight
} from 'react-native';

export default function ContactScreen() {
    // const [email, setEmail] = useState("");
    // const { register, handleSubmit, formState: { errors } } = useForm();
    // const onSubmit = data => console.log(data);
    // console.log(errors);

    const {
        register,
        formState: { errors },
        handleSubmit
      } = useForm({
        criteriaMode: "all"
      });
      const onSubmit = (data) => console.log(data);
    
 
    return (
        <>
            <View style={styles.homeContainer}>
                <Text style={styles.homeHeading}>Help & Support</Text>
                <Text style={styles.homeContent}>Let our dedicated enterprise team deliver the best service. We are here to help maximise your business growth and take your website to a whole new level.</Text>
                <Text style={styles.homeContent}>Have more questions on eShopping services offered? Drop us an email and we will get back to you as soon as possible.</Text>
                <SafeAreaView>
                <form onSubmit={handleSubmit(onSubmit)}>
                
                <input
                    style={styles.input}
                    {...register("multipleErrorInput", {
                    required: "This input is required.",
                    pattern: {
                        value: /\d+/,
                        message: "This input is number only."
                    },
                    minLength: {
                        value: 11,
                        message: "This input must exceed 10 characters"
                    }
                    })}
                />
                <ErrorMessage
                    errors={errors}
                    name="multipleErrorInput"
                    render={({ messages }) => {
                    console.log("messages", messages);
                    return messages
                        ? Object.entries(messages).map(([type, message]) => (
                            <p key={type}>{message}</p>
                        ))
                        : null;
                    }}
                />
                
                <input type="submit" />
                </form>


                {/* <form onSubmit={handleSubmit(onSubmit)}>
                    <input type="text" placeholder="First name" {...register("First name", {required: true, maxLength: 80})} />
                    <input type="text" placeholder="Last name" {...register("Last name", {required: true, maxLength: 100})} />
                    <input type="text" placeholder="Email" {...register("Email", {required: true, pattern: /^\S+@\S+$/i})} />
                    <input type="tel" placeholder="Mobile number" {...register("Mobile number", {required: true, minLength: 6, maxLength: 12})} />
                    <select {...register("Title", { required: true })}>
                        <option value="Mr">Mr</option>
                        <option value="Mrs">Mrs</option>
                        <option value="Miss">Miss</option>
                        <option value="Dr">Dr</option>
                    </select>

                    <input {...register("Developer", { required: true })} type="radio" value="Yes" />
                    <input {...register("Developer", { required: true })} type="radio" value="No" />

                    <input type="submit" />
                    </form> */}
                </SafeAreaView>
            </View>
        </>
    );
}

const styles = StyleSheet.create({
    homeContainer: {
      backgroundColor: '#ffffff',
      margin: 12,
      padding: 16,
      fontFamily: 'arial',
      color: '#666',
      borderRadius: 16,
    shadowOpacity: 0.2,
    shadowRadius: 4,
    shadowColor: 'black',
    shadowOffset: {
      height: 0,
      width: 0,
    },
    elevation: 1,
    },
    homeHeading: {
      fontSize: 16,
      fontWeight: 'bold',
      color: '#666'
    },
    
    homeContent: {
      fontSize: 14,
      paddingTop:6,
      paddingBottom: 6,
      lineHeight: 20
    },
    emailInput: {
        width: 250,
        height: 50,
        borderWidth: 1,
        borderRadius: 15,
        borderColor: 'black',
        padding: 2,
    },
    button: {
        backgroundColor: 'lightgreen',
        borderRadius: 15,
        marginTop: 25,
        padding: 10,
        alignItems: 'center'
      },
      buttonText: {
        color: 'white'
    },
    input: {
        borderColor: '#cccccc',
        borderWidth: 1,
        padding: 10,
    }
});
