import React from 'react';
import { View, StyleSheet } from 'react-native';
import {
    useTheme,
    Avatar,
    Title,
    Caption,
    Paragraph,
    Drawer,
    Text,
    TouchableRipple,
    Switch
} from 'react-native-paper';
import {
    DrawerContentScrollView,
    DrawerItem
} from '@react-navigation/drawer';

import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import FontAwesome5 from 'react-native-vector-icons/FontAwesome5';
import { getCategories } from '../services/categoryService';
//import{ AuthContext } from '../components/context';

export function DrawerContent(props) {
    
    const paperTheme = useTheme();

    // const { signOut, toggleTheme } = React.useContext(AuthContext);

    return(
        <View style={{flex:1}}>
            <View style={styles.userInfoSection}>
                <View style={{flexDirection:'row',marginTop: 15}}>
                    <Avatar.Image 
                        source={{
                            uri: 'https://media.licdn.com/dms/image/C5603AQGlvzG3w0LfDg/profile-displayphoto-shrink_800_800/0/1603518154735?e=1680134400&v=beta&t=7_PU8UEhl5tDYxVxjgWt1e4FTMNG3cCCDMlyGFebdxU'
                        }}
                        size={50}
                    />
                    <View style={{marginLeft:15, flexDirection:'column'}}>
                        <Title style={styles.title}>Nithi Jai</Title>
                        <Caption style={styles.caption}>@nithi_jai</Caption>
                    </View>
                </View>
            </View>

            <View style={styles.drawerSectionContr}><Text style={styles.drawerSectionTitle}>Shop by Category</Text></View>

            <DrawerContentScrollView {...props}>
                <View>
                    <Drawer.Section style={styles.drawerSection}>
                        <DrawerItem
                            icon={({focused}) => (
                                <FontAwesome5 
                                name="comment"
                                size={focused ? 25 : 20}
                                color={focused ? '#7fad39' : '#999999'}
                                />
                            )}
                            label="Contact Us"
                            onPress={() => {props.navigation.navigate('ContactUs_Screen')}}
                        />
                    </Drawer.Section>
                </View>
            </DrawerContentScrollView>

            <View style={styles.drawerSectionContr}><Text style={styles.drawerSectionTitle}>Preferences</Text></View>
            
            <Drawer.Section style={styles.bottomDrawerSection}>
                <DrawerItem
                    icon={({focused}) => (
                        <FontAwesome5 
                        name="home"
                        size={focused ? 25 : 20}
                        color={focused ? '#7fad39' : '#999999'}
                        />
                    )}
                    label="Home"
                    onPress={() => {props.navigation.navigate('Home_Screen')}}
                />
                <DrawerItem
                    icon={({focused}) => (
                        <FontAwesome5 
                        name="globe"
                        size={focused ? 25 : 20}
                        color={focused ? '#7fad39' : '#999999'}
                        />
                    )}
                    label="About Us"
                    onPress={() => {props.navigation.navigate('AboutUs_Screen')}}
                />
                <DrawerItem
                    icon={({focused}) => (
                        <FontAwesome5 
                        name="comment"
                        size={focused ? 25 : 20}
                        color={focused ? '#7fad39' : '#999999'}
                        />
                    )}
                    label="Help & Support"
                    onPress={() => {props.navigation.navigate('ContactUs_Screen')}}
                />
                <DrawerItem 
                    icon={({color, size}) => (
                        <Icon 
                        name="exit-to-app" 
                        color={color}
                        size={size}
                        />
                    )}
                    label="Sign Out"
                />
            </Drawer.Section>
        </View>
    );
}

const styles = StyleSheet.create({
    drawerContent: {
      
    },
    drawerItem: {
        height: 20,
    },
    userInfoSection: {
      paddingLeft: 20,
    },
    title: {
      fontSize: 16,
      marginTop: 3,
      fontWeight: 'bold',
      
    },
    drawerSectionTitle: {
      fontSize: 12,
      color: '#ffffff',
      fontWeight: 'bold',
      textTransform: 'uppercase'
    },
    drawerSectionContr: {
        marginTop: 20,
        marginLeft: 10,
        marginRight: 10,
        marginBottom: 0,
        borderRadius: 5,
        padding: 10,
        backgroundColor: '#7fad39'        
    },
    caption: {
      fontSize: 14,
      lineHeight: 14,
    },
    row: {
      marginTop: 20,
      flexDirection: 'row',
      alignItems: 'center',
    },
    section: {
      flexDirection: 'row',
      alignItems: 'center',
      marginRight: 15,
    },
    paragraph: {
      fontWeight: 'bold',
      marginRight: 3,
    },
    drawerSection: {
        // borderTopColor: '#f4f4f4',
        // borderTopWidth: 1,
        // marginTop: 15,
    },
    bottomDrawerSection: {
        marginBottom: 15,
        // borderTopColor: '#f4f4f4',
        // borderTopWidth: 1
    },
    preference: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      paddingVertical: 12,
      paddingHorizontal: 16,
    },
  });
