import React, { useState } from 'react';
import {
    StyleSheet,
    View,
    Text,
    SafeAreaView,
    TextInput,
    TouchableHighlight
} from 'react-native';

export default function ContactScreen() {
    const [email, setEmail] = useState("");
 
 const handleSubmit = () => {
     console.log(email);
 }
    return (
        <>
            <View style={styles.homeContainer}>
                <Text style={styles.homeHeading}>Help & Support</Text>
                <Text style={styles.homeContent}>Let our dedicated enterprise team deliver the best service. We are here to help maximise your business growth and take your website to a whole new level.</Text>
                
                <SafeAreaView>
                    <TextInput
                        onChangeText={(text) => setEmail(text)}
                        value={email}
                        style={styles.emailInput}
                        placeholder="Enter your email"
                    />
                    <TouchableHighlight onPress={handleSubmit} style={styles.button}>
                        <Text style={styles.buttonText}>Submit</Text>
                    </TouchableHighlight>
                </SafeAreaView>
            </View>
        </>
    );
}

const styles = StyleSheet.create({
    homeContainer: {
      backgroundColor: '#ffffff',
      margin: 12,
      padding: 16,
      fontFamily: 'arial',
      color: '#666',
      borderRadius: 16,
    shadowOpacity: 0.2,
    shadowRadius: 4,
    shadowColor: 'black',
    shadowOffset: {
      height: 0,
      width: 0,
    },
    elevation: 1,
    },
    homeHeading: {
      fontSize: 16,
      fontWeight: 'bold',
      color: '#666'
    },
    
    homeContent: {
      fontSize: 14,
      paddingTop:6,
      paddingBottom: 6,
      lineHeight: 20
    },
    emailInput: {
        width: 250,
        height: 50,
        borderWidth: 1,
        borderRadius: 15,
        borderColor: 'black',
        padding: 2,
    },
    button: {
        backgroundColor: 'lightgreen',
        borderRadius: 15,
        marginTop: 25,
        padding: 10,
        alignItems: 'center'
      },
      buttonText: {
        color: 'white'
    }
});
